package ru.limydesign.plugins.yandex.translate;import com.intellij.openapi.actionSystem.AnAction;import com.intellij.openapi.actionSystem.AnActionEvent;import java.util.Locale;import java.util.ResourceBundle;import java.util.prefs.Preferences;/** * Created by Arsen Bespalov on 13.11.2016. * Показывает диалоговое окно переводчика. */public class YandexTranslateDialog extends AnAction {    private static final ResourceBundle MESS = ResourceBundle.getBundle("Messages", Locale.getDefault());    @Override    public void actionPerformed(AnActionEvent e) {        ResultDialog dialog;        Preferences preferences;        try {            preferences = Preferences.userNodeForPackage(ru.limydesign.plugins.yandex.translate.ResultDialog.class);            String langFrom = preferences.get("langFrom", MESS.getString("langFrom"));            String langTo = preferences.get("langTo", MESS.getString("langTo"));            dialog = ResultDialog.createDialog(MESS.getString("title"), null, null);            dialog.setFromLangBox(langFrom);            dialog.setToLangBox(langTo);        } catch (Exception e1) {            e1.printStackTrace();        }    }}